import { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
    const [notes, setNotes] = useState([]);
    const [title, setTitle] = useState("");
    const [body, setBody] = useState("");
    const [editing, setEditing] = useState(null);

    const loadNotes = async () => {
        const res = await axios.get("http://localhost:5000/notes");
        setNotes(res.data);
    };

    const handleAddOrUpdate = async () => {
        if (editing !== null) {
            await axios.put(`http://localhost:5000/notes/${editing}`, { title, body });
            setEditing(null);
        } else {
            await axios.post("http://localhost:5000/notes", { title, body });
        }
        setTitle("");
        setBody("");
        loadNotes();
    };

    const handleEdit = (note) => {
        setTitle(note.title);
        setBody(note.body);
        setEditing(note.id);
    };

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:5000/notes/${id}`);
        loadNotes();
    };

    useEffect(() => {
        loadNotes();
    }, []);

    return (
        <div className="container mt-5">
            <h2>Dashboard</h2>
            <input className="form-control mb-2" placeholder="Note Title" value={title} onChange={(e) => setTitle(e.target.value)} />
            <textarea className="form-control mb-2" placeholder="Note Body" value={body} onChange={(e) => setBody(e.target.value)}></textarea>
            <button className="btn btn-success mb-4" onClick={handleAddOrUpdate}>
                {editing ? "Update Note" : "Add Note"}
            </button>

            <div className="list-group">
                {notes.map(note => (
                    <div key={note.id} className="list-group-item">
                        <h5>{note.title}</h5>
                        <p>{note.body}</p>
                        <button className="btn btn-sm btn-warning me-2" onClick={() => handleEdit(note)}>Edit</button>
                        <button className="btn btn-sm btn-danger" onClick={() => handleDelete(note.id)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
}
